function displayPopupWithAnimation(t){t.style.display="flex"}function hidePopupWithAnimation(t){t.style.display="none",cfmMsg.style.opacity=0,cfmMsg.style.transform="translateY(-20px)"}function showError(t){cfmMsg.textContent=t,cfmMsg.style.opacity=1,cfmMsg.style.transform="translateY(0)"}function hideError(){cfmMsg.style.opacity=0,cfmMsg.style.transform="translateY(-20px)"}function isValidEmail(t){const e=/^[^\s@]+@[^\s@]+\.[^\s@]+$/;return e.test(t)}const confirmButton=document.getElementById("confirm"),popup=document.getElementById("Register_cfm"),popupEmail=document.getElementById("Email_cfm"),editButton=document.getElementById("edit"),submitButton=document.getElementById("submit"),userEmailInput=document.getElementById("user_mail"),cfmMsg=document.getElementById("cfm_msg"),emailForm=document.getElementById("emailForm");confirmButton.addEventListener("click",t=>{t.preventDefault(),isValidEmail(userEmailInput.value)?(popupEmail.textContent=userEmailInput.value,displayPopupWithAnimation(popup)):showError("正しいメールアドレスを入力してください。")}),editButton.addEventListener("click",()=>{hidePopupWithAnimation(popup),hideError()}),submitButton.addEventListener("click",function(t){t.preventDefault(),emailForm.action="/components/templates/register_mail/temporary_register_process.php",emailForm.submit(),hidePopupWithAnimation(popup),hideError()}),userEmailInput.addEventListener("input",()=>{confirmButton.disabled=!isValidEmail(userEmailInput.value)});